import React from 'react';
import '../styles/tour-details.css';
import { Container, Row, Col, From, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import tourData from "../assets/data/tours";

const TourDetails = () => {
  const { id } = useParams();

  // Output the ID obtained from useParams to check its value
  console.log("ID from useParams:", id);

  // Find the tour with the specified id
  const tour = tourData.find(tour => tour.id === id);

  // Output the tour object to check its content
  console.log("Tour object:", tour);

  // Check if tour exists and has all required properties
  if (!tour || !tour.photo || !tour.title) {
    return <p>Tour details not found.</p>;
  }

  // Destructure properties from tour object
  const { photo, title, desc, price, reviews, city, distance, maxGroupSize } = tour;

  return (
    <>
      <section>
        <Container>
          <Row>
            <Col lg="8">
              <div className="tour__content">
                <img src={photo} alt={title} />
                <div className="tour__info">
                  <h2>{title}</h2>
                </div>
              </div>
            </Col>
          </Row>
        </Container>
      </section>
    </>
  );
};

export default TourDetails;
